import React,{useEffect,useState,useContext} from 'react'
import Post from './postcontent'
import Search from './search'
import Content from './postlist/postindex'
import {Context} from '../../editusermessage/context/ins-context'
import UseGetData from '../../public/useDateGet'
import {connect} from 'react-redux'
import {GET} from './redux/action'
import  Loading from '../../public/loading.js'




function Index(props) {
    const url = 'http://localhost:3001/getphotos';
    const [state,fetching,error] = UseGetData(undefined,url);
    useEffect(() => {
         if(Array.isArray(state)) props.action(GET(state));
    })
    
    const scrollEv = (e) => {                           //滚动按钮显示事件
        let gotop = document.querySelector('.gotop');
        if(e.target.scrollTop>window.innerHeight/2){
           gotop.hidden = false;
        }else{
            gotop.hidden = true;
        }
    }

    const scrollTOP = (e) => {                // 滚动到顶部事件
       e.target.closest('.main').scrollTop = 0;
       e.target.closest('.gotop').hidden = true;
    }
    return (
        <div className='main' onScroll={scrollEv}>               
                <Context.Provider value={props.data}>
                    {
                        error?<div>something is wrong</div>:
                        props.data[0]?<>
                                        <Post account={props.account} />
                                        <Search/>
                                        <Content message={props.message}/>
                                        <div className="gotop" hidden onClick={scrollTOP}>
                                            <i className="iconfont icon-rising"></i>
                                        </div>
                                   </>: <Loading/>
                    }
                 </Context.Provider>
            <style jsx> {`
                        .main {
                            height:100%;
                            width:50%;
                            background:#eeeef4;
                            overflow:auto;
                            padding:2vh 4vh 0 4vh;
                            position:relative;
                        }
                        .main::-webkit-scrollbar {
                            display:none;
                        }
                        .gotop {
                            position:fixed;
                            top:49vh;
                            right:29vw;
                            width:60px;
                            height:60px;
                            animation:bu .5s 1;
                        }
                        @keyframes bu {
                            0% {
                                transform:scale(0)
                            }
                            100% {
                                transform:scale(1)
                            }
                        }
                        .gotop>i {
                          font-size:2rem;
                          color:#1a79ff;
                          background:white;
                          border-radius:0.5rem;
                          box-shadow: 0 0 2px rgba(0,0,0,0.3);
                        }
                        `}
             </style>
            
        </div>
    )
}



const state = (store) =>( {data:store});
const dispatch = dispatch =>( {action:dispatch});
export default connect(state,dispatch)(Index)
