import React,{useEffect} from 'react'
import CommentList from './features-comment'
import Router  from 'next/router'

function Features({id,comment,message}) {
    let formRef = React.createRef();
    let buAllRef;

    useEffect(() => {                      //判断帖子中是否收藏 点赞过
       if(message){
            let result = message.like.findIndex(e => e.postId == id);
            if(result != -1 ){
            buAllRef.children[0].children[0].classList.add('icon-dianzan1');
            }
            let result2 = message.save.findIndex(e => e.postId == id);
            if(result2 != -1 ){
            buAllRef.children[1].children[0].classList.add('icon-shoucang1');
            }
       }       
    },[])
    const commentEv = () => {        //打开评论界面按钮事件
           let form = formRef.current;
           if(getComputedStyle(form).display == 'none'){
                form.style.display = 'flex';           
                if(!form.hidden) form.children[0].focus();
           }else{
                form.style.display = 'none';           
           }
           
    }
    const saveEv = async (e) => {
        let target = e.target.closest('.save').children[0]; //定义save按钮元素
        target.classList.toggle('icon-shoucang1');
        if(target.classList.contains('icon-shoucang1')){     
            let request = await fetch(`http://localhost:3001/post/save/${id}`,{
                method:'POST',
                credentials: "include",
            })
            if(request.status == 404) Router.push('/')
            if(request.status == 303) target.classList.remove('icon-shoucang1');
            let res =await  request.json();
            alert(res);
        }else{
            
            let request = await fetch(`http://localhost:3001/post/save/remove/${id}`,{
                method:'POST',
                credentials: "include",
            })
            if(request.status == 404) Router.push('/')
            let res =await  request.json();
            alert(res);
        }
    }
    const likeEv = async (e) => {
        let target = e.target.closest('.like').children[0]; //定义like按钮元素
        target.classList.toggle('icon-dianzan1');
        if(target.classList.contains('icon-dianzan1')){     
            let request = await fetch(`http://localhost:3001/post/like/${id}`,{
                method:'POST',
                credentials: "include",
            })
            if(request.status == 404) Router.push('/')
            if(request.status == 303) target.classList.remove('icon-dianzan1');
            let res =await  request.json();
            alert(res);
        }else{
            
            let request = await fetch(`http://localhost:3001/post/like/remove/${id}`,{
                method:'POST',
                credentials: "include",
            })
            if(request.status == 404) Router.push('/')
            let res =await  request.json();
            alert(res);
        }

        
       
    }
    return (
       <div className="features">
           <CommentList ref ={formRef} id ={id} comment={comment}/>
           <div className="button-All" ref={ref => buAllRef = ref}>
                <div className="like" onClick={likeEv}>                  
                    <i className='iconfont icon-dianzan'></i>
                    <h4>Like</h4>
                </div>
                <div className="save" onClick={saveEv}>
                    <i className='iconfont icon-shoucang'></i>
                    <h4>save</h4>
                </div>
                <div className="comment" onClick={commentEv}>
                    <i className='iconfont icon-pinglun'></i>
                    <h4>comment</h4>
                </div>
                <div className="share">
                    <i className='iconfont icon-fenxiang'></i>
                    <h4>share</h4>
                </div>
            </div>
            <style jsx> {`                
                  i {
                      font-size:30px;
                      margin-right:6px;
                  }
                  .icon-dianzan1 {
                      color:#E84F43;
                      animation:click 0.5s 1;
                  }
                  .icon-shoucang1{
                      color:#FFF710;
                      animation:click 0.5s 1;
                  }


                  @keyframes click {
                      0% {
                          transform:scale(0)
                      }
                      100% {
                          transform:scale(1)
                      }
                  }
                  .button-All {
                      width:100%;
                      display:flex;
                      height:8vh;
                      align-items:center;
                      justify-content:space-between;
                  }
                  .button-All>div {
                       display:flex;
                       flex:1;
                       height:6vh;
                       justify-content:center;
                       align-items:center;
                       margin-right:4vh;
                       opacity:0.5;
                  }
                  .button-All>div:hover {
                      opacity:1;
                  }
                  .like {
                      margin-left:4vh;
                  }
                  .share svg {
                        margin-right:0.4vh;
                  }
                  svg {
                      height:50%;
                  }
                        `}
       </style>
        </div> 
    )
}


export default Features
