import React,{useState} from 'react'
import BuddyList from './buddyList'

function Index() {
    const [state, setState] = useState(false);
    const clickEv = () => {
       setState(true)
    }
    return (
        <div className='chat testca'>
            <div className="container">
                 <div className="chat-message">
                     <div className="math">
                         2
                     </div>
                     <i className='iconfont icon-xiaoxizhongxin'></i>
                 </div>
                 <div className="buddy" onClick={clickEv}>
                    <i className='iconfont icon-shouji'></i>
                 </div>
            </div>
            {state && <BuddyList/>}
            <style jsx>
               {`
               .chat {
                 height:100%;
                 width:30vw;
                 background:white;
               }
               .testca {
                   height:10%;
                   width:20vw;
                   position:fixed;
                   right:0;
                   top:0;
                   border-radius:24px;
               }
               .container {
                   display:flex;
                   height:100%;
                   justify-content:center;
                   align-items:center;
               }
               .chat-message {
                   position:relative;
               }
               .math {
                   position:absolute;
                   right:1rem;
                   top:-0.2rem;
                   background:red;
                   width:22px;
                   height:22px;
                   color:white;
                   border-radius:6px;
                   text-align:center;
                   line-height:22px;
               }
               i {
                   font-size:4rem;
                   color:#1a79ff;
                   opacity:0.8;
                   margin:0 1rem;
                   transition:.4s;
               }
               i:hover {
                  opacity:1;
                  font-size:4.4rem;
               }
               `}
            </style>
        </div>
    )
}

export default Index
