import React,{useState} from 'react'
import {connect} from 'react-redux'
import {ADD} from '../../redux/action'


const  FeaturesComment = React.forwardRef((props, ref) => {
      const [message, setMessage] = useState(''); 
      const [commentList,setCommentList] = useState(props.comment);

      const addComment = async (e) => {    // æ·»åŠ è¯„è®ºäº‹ä»¶
        e.preventDefault();
        let commentData = new FormData()
        commentData.append('comment',message);
        let request = await  fetch(`http://localhost:3001/post/comment/${props.id}`,{
            method:'POST',
            body:commentData,    
            credentials: "include",        
        })
        let res = await request.json();
        if(typeof res == 'object') {
            setCommentList(res)
        }
    }
    const inputEv = (e) => {
        if(e.target.value.length>0){
             ref.current.children[1].classList.add('pink');
        }else{
            ref.current.children[1].classList.remove('pink');
        }
        setMessage(e.target.value)
    }
    return (
        <div className="comment-list">
               <ul>
                   {commentList.map(e => <li key={e}>{e.comment}ğŸ’—</li>)}
                   {/* <li> í•‘í¬ ğŸ’—</li>
                   <li> so beautiful</li>
                   <li> í•‘í¬ ğŸ’—</li>
                   <li> YA ALLAHHH DEKET BANGETTTT</li>
                   <li> í•‘í¬ ğŸ’—</li> */}
               </ul>
               <form ref={ref} onSubmit= {addComment} hidden >
                   <input type="text" name="comment" 
                     onInput={inputEv}
                     value ={ message }
                     required
                     placeholder='è¾“å…¥å†…å®¹'
                     />
                     <div className="button" onClick={(e) => {
                            e.target.closest('.button').nextElementSibling.click();
                         }}>
                         <i className='iconfont icon-send'></i>
                     </div>
                     <input type="submit" value="add" hidden/>
               </form>
               <style jsx>
                   {`                  
                  .button {                      
                      flex:1;
                      display:flex;
                      justify-content:center;
                      align-items:center;
                      border-radius:50%;
                  }
                  .button:hover>i {
                      transform:scale(1.1);
                      background:#1a79ff;
                      color:white;
                  }
                  .button>i {
                      font-size:1.6rem; 
                      padding:10px;
                      border-radius:50%;                     
                  }
                  form {
                      width:100%;
                      height:6vh;
                      background:white;                     
                      border-radius:24px;
                      margin:4vh 0 0 ;                
                      display:none;
                  }  
                  input[type=submit] {
                    color:white;
                  }

                  form>input {
                      flex:9;
                      text-align:center;
                      height:100%;                      
                      border: 2px solid #f4f6f8;
                      font-size:3vh;
                      padding:0 2vh;
                      outline:none;
                  }
                  input[type=text] {
                      color:white;
                  }
                  form>input:focus {
                      background:#eeeef4;
                  }
                  .comment-list {
                      width:100%;
                      margin:4vh 0 2vh ;
                  } 
                  ul {
                      width:80%;
                      margin-left:10%;
                      list-style:none;
                  }
                  li {
                      width:100%;
                      height:2vh;
                      margin:1vh;
                      text-align:center;
                  }
                   `}
               </style>
        </div>
    )
    })
const dispatchToProps = dispatch => ({
    Click(post) {
        dispatch(ADD(post))
    }
})
const stateToProps = state => ({       });




export default  connect(null,dispatchToProps,null,{forwardRef: true})(FeaturesComment)
